import { useEffect, useState, useRef } from 'react'
import RecipeCard from "./components/RecipeCard.js"
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import 'bootstrap/dist/css/bootstrap.min.css';

import './index.css';

const recipes = [
  { id: 1, title: 'Recipe 1' },
  { id: 2, title: 'Recipe 2' },
  { id: 3, title: 'Recipe 3' }
];

function ImageRenderer({ imageBinary }) {
  const [imageUrl, setImageUrl] = useState(null);

  useEffect(() => {

    const blob = new Blob([imageBinary]);
    const url = URL.createObjectURL(blob);
    setImageUrl(url);
    return () => URL.revokeObjectURL(url);
  }, [imageBinary]);

  return (
    <div>
      {imageUrl ? (
        <img src={imageUrl} alt="Rendered Image" />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

const App = () => {

  // example image
  function getExampleImage() {
    return '';
}
  var str = "hello";
  var exImg = "data:image/jpeg;base64,";


  var initialRecipes = []
  var exIngredients = ["stuff", "other stuff"]
  initialRecipes.push(
    <RecipeCard name="Recipe1" topMargin={0} ingredients={exIngredients} pantry = {exIngredients} image = {exImg}/>
  );
  initialRecipes.push(
    <RecipeCard name="Recipe2" topMargin={20} ingredients={exIngredients} pantry = {exIngredients} image = {exImg}/>
  );
  initialRecipes.push(
    <RecipeCard name="Recipe3" topMargin={40} ingredients={exIngredients} pantry = {exIngredients} image = {exImg}/>
  );
  initialRecipes.push(
    <RecipeCard name="Recipe4" topMargin={60} ingredients={exIngredients} pantry = {exIngredients} image = {exImg}/>
  );


  const [snapshotBinary, updateSnapshotBinary] = useState(null)

  const [fridgeSnapshot, setFridgeSnapshot] = useState(new Image())

  const[curRecipes, setRecipes] = useState(initialRecipes)

  const[itemCount, setItemCount] = useState(1)

  /* function hexToBase64(str) {
    return btoa(String.fromCharCode.apply(null, str.replace(/\r|\n/g, "").replace(/([\da-fA-F]{2}) ?/g, "0x$1 ").replace(/ +$/, "").split(" ")));
  } */
  const MyDropdown = () => {

    const handleOneItem = () => {
      setItemCount(1)
      console.log(itemCount)
    }

    const handleTwoItems = () => {
      setItemCount(2)
      console.log(itemCount)
    }

    const handleThreeItems = () => {
      setItemCount(3)
      console.log(itemCount)
    }


    return (
      <Dropdown> 
        <Dropdown.Toggle variant="success"> 
          Item Count 
        </Dropdown.Toggle> 
        <Dropdown.Menu> 
          <Dropdown.Item onClick={handleOneItem}> 
            1 
          </Dropdown.Item> 
          <Dropdown.Item onClick={handleTwoItems}> 
            2 
          </Dropdown.Item> 
          <Dropdown.Item onClick={handleThreeItems}> 
            3 
          </Dropdown.Item> 
        </Dropdown.Menu> 
      </Dropdown>
    )
  } 
  const fetchSnapshot = async () => {
    fetch('http://localhost:4000/api/getSnapshot').then(
        (response) => {
            if (!response.ok) {
                throw new Error('Error getting snapshot')
            }
            return response.json();
        }).then( (data) => {
          // console.log(data[0].data)
          //console.log(data[0].number)
          if (data[0].data == snapshotBinary) {
            console.log("same")
          }
          updateSnapshotBinary(data[0].data)
          //console.log(data[0].data)
          //console.log(data[0].number)
          // console.log(snapshotBinary)
        })
  }


  const fetchRecipe = async () => {
    fetch('http://localhost:4000/api/getRecipes?' + new URLSearchParams({
      totalItems:itemCount
    })).then(
        (response) => {
            if (!response.ok) {
                throw new Error('Error getting snapshot')
            }
            return response.json();
        }).then( (data) => {
          var newRecipes = [];
          var offset = 0;

          var map = {};
          var greenSpectrum = ['#009c1a', '#5eb84a', '#68e376', '#8aeb95', '#abebb2'];
          var curCol = '#26cc00';
          
          if(data.length == 0){
            newRecipes.push(
              <div className="recipe-card">
                <h2>No items ur broke</h2>
              </div>
            )
          }else{
            for(let i = 0; i < data.length; i++){
              const rcp = data[i];
              if(map[rcp.name] == undefined){
                map[rcp.name] = true;

                if(i<=4){
                  curCol = greenSpectrum[i];
                }else{
                  curCol = '#FFFFFF';
                }
                newRecipes.push(
                  <RecipeCard name={rcp.name} topMargin={offset} ingredients={rcp.includedIngredients} pantry = {rcp.missingIngredients} image = {rcp.image} myColor={curCol}/>
                )
                offset = offset + 20;
              }           
            }
          }

          setRecipes(newRecipes);
        })
  }

  const handleGenerateRecipe = () => {
    console.log("FETCHED")
    fetchRecipe();
  }

  // RecipeCard = ({name, topMargin, ingredients, pantry, image})

  //newSnapshot.alt = "fridge snapshot";
      //newSnapshot.style.width = '80%';
      //newSnapshot.style.height = '80%';

  useEffect( () => {
    console.log("BEGINNING OF USEEFFECT")
    const repeatFunction = async () => {
      try{
        await fetchSnapshot();       
        setFridgeSnapshot({
          src: "data:image/jpeg;base64,"+snapshotBinary,
        });        

        //console.log(snapshotBinary)
      } catch(error) {
        console.error('Error fetching snapshot:', error);
      }
    }

    var repeatInterval = 3000;
    const intervalId = setInterval(repeatFunction, repeatInterval);

    return () => clearInterval(intervalId);
  }, [snapshotBinary])

  return (
    <div className="app">
      
      <div className="leftContainer">
        <div className="welcome">
          <h3>Welcome! Become more sustainable by reducing food waste, all with the click of a button.</h3>
        </div>
        <div className="fridge_snapshot">
          <h2>Real-time fridge snapshot</h2>
          <img src = {fridgeSnapshot.src} alt = {fridgeSnapshot.alt} style = {{width:'80%', height:'80%', padding:'10px'}}/>
        </div>
        <MyDropdown/>
      </div>
      <div className="recipes">
        <button className="generate-button" onClick={handleGenerateRecipe}>Generate Recipe</button>
        {curRecipes}
      </div>
    </div>
  );
};

export default App;


